import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../../styles/WhyRGM.css";

/* HERO SLIDESHOW IMAGES */
const heroImages = [
  require("../../assets/gallery/images/gallery10.jpg"),
  require("../../assets/gallery/images/gallery11.jpg"),
  require("../../assets/gallery/images/gallery12.jpg"),
  require("../../assets/gallery/images/gallery13.jpg"),
  require("../../assets/gallery/images/gallery14.jpg")
];

/* MAIN PAGE SLIDESHOW IMAGES */
const cultureImages = [
  require("../../assets/gallery/images/gallery18.jpg"),
  require("../../assets/gallery/images/gallery19.jpg"),
  require("../../assets/gallery/images/gallery20.jpg"),
  require("../../assets/gallery/images/gallery21.jpg"),
  require("../../assets/gallery/images/gallery22.jpg")
];

function WhyRGM() {
  const [heroIndex, setHeroIndex] = useState(0);
  const [cultureIndex, setCultureIndex] = useState(0);

  /* HERO SLIDESHOW */
  useEffect(() => {
    const interval = setInterval(() => {
      setHeroIndex((prev) => (prev + 1) % heroImages.length);
    }, 4500);
    return () => clearInterval(interval);
  }, []);

  /* CULTURE SLIDESHOW */
  useEffect(() => {
    const interval = setInterval(() => {
      setCultureIndex((prev) => (prev + 1) % cultureImages.length);
    }, 3500);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="why-rgm-page">

      {/* HERO */}
      <section
        className="why-hero"
        style={{ backgroundImage: `url(${heroImages[heroIndex]})` }}
      >
        <div className="why-hero-overlay">
          <h1>
            Why <span>RGM</span> Family
          </h1>
          <p>
            You‚Äôre not just joining a company ‚Äî  
            you‚Äôre becoming part of a family that grows together.
          </p>

          <Link to="/joinRGM/ApplyToday" className="apply-btn">
            Apply Now
          </Link>
        </div>
      </section>

      {/* INTRO */}
      <section className="why-intro">
        <p>
          RGM may be a growing company, but our foundation is built on
          <strong> trust, respect, and people-first values</strong>.
          We believe when our drivers and team members succeed ‚Äî everyone wins.
        </p>
      </section>

      {/* CULTURE SECTION */}
      <section className="culture-section">
        <div className="culture-text">
          <h2>Life at RGM</h2>
          <p>
            At RGM, we treat every driver, employee, and partner like family.
            We focus on long-term relationships, clear communication,
            and genuine support ‚Äî on and off the road.
          </p>
        </div>

        <div className="culture-image">
          <img src={cultureImages[cultureIndex]} alt="RGM Culture" />
        </div>
      </section>

      {/* VALUES */}
      <section className="values-section">
        <h2>Our Core Values</h2>

        <div className="values-grid">
          <div className="value-card">
            <h3>ü§ù Family First</h3>
            <p>Your voice matters. Your growth matters.</p>
          </div>

          <div className="value-card">
            <h3>üõ° Integrity & Trust</h3>
            <p>We do business honestly and transparently.</p>
          </div>

          <div className="value-card">
            <h3>üöõ Driver Focused</h3>
            <p>Drivers are the backbone of RGM.</p>
          </div>

          <div className="value-card">
            <h3>üìà Grow Together</h3>
            <p>We build long-term success, not short-term wins.</p>
          </div>

          <div className="value-card">
            <h3>üß† Smart Technology</h3>
            <p>Modern tools with a human touch.</p>
          </div>

          <div className="value-card">
            <h3>üíô Support Beyond Work</h3>
            <p>Your well-being matters inside and outside work.</p>
          </div>
        </div>
      </section>

      {/* WHY RGM LIST */}
      <section className="why-list-section">
        <h2>Why Choose RGM?</h2>
        <ul>
          <li>‚úî People-first company culture</li>
          <li>‚úî Reliable & transparent operations</li>
          <li>‚úî Driver-focused management</li>
          <li>‚úî Growth & stability</li>
          <li>‚úî Respect, fairness & opportunity</li>
        </ul>
      </section>

      {/* CTA */}
      <section className="why-cta">
        <h2>Let‚Äôs Talk About Your Future With RGM</h2>
        <p>Join a company that treats you like family.</p>

        <Link to="/joinRGM/ApplyToday" className="apply-btn large">
          Apply Now
        </Link>
      </section>

    </div>
  );
}

export default WhyRGM;
