import React, { useState, useEffect, useRef } from "react";
import "./Gallery.css";

/* AUTO LOAD MEDIA */
const imgCtx = require.context("../assets/gallery/images", false, /\.(jpg|png|jpeg|webp)$/);
const vidCtx = require.context("../assets/gallery/videos", false, /\.(mp4)$/);

/* SMART CATEGORY DETECTION */
const detectCategory = (name) => {
  const n = name.toLowerCase();
  if (n.includes("fleet") || n.includes("truck")) return "Fleet";
  if (n.includes("driver") || n.includes("team")) return "Drivers";
  if (n.includes("road") || n.includes("highway")) return "On Road";
  if (n.includes("yard") || n.includes("dock")) return "Warehouse";
  return "General";
};

/* PREPARE MEDIA */
const images = imgCtx.keys().map((k) => ({
  src: imgCtx(k),
  type: "image",
  category: detectCategory(k),
}));

const videos = vidCtx.keys().map((k) => ({
  src: vidCtx(k),
  type: "video",
  category: "Videos",
}));

const categories = ["All", "Fleet", "Drivers", "On Road", "Warehouse", "Videos"];

const Gallery = () => {
  const [active, setActive] = useState("All");
  const [lightbox, setLightbox] = useState(null);
  const [videoModal, setVideoModal] = useState(null);
  const [currentVideo, setCurrentVideo] = useState(0);
  const [currentSlide, setCurrentSlide] = useState(0);
  const videoRef = useRef(null);

  /* VIDEO LOOP AUTOPLAY */
  useEffect(() => {
    if (!videos.length) return;
    const vid = videoRef.current;
    const handleEnded = () => {
      setCurrentVideo((prev) => (prev + 1) % videos.length);
    };
    vid?.addEventListener("ended", handleEnded);
    return () => vid?.removeEventListener("ended", handleEnded);
  }, []);

  /* HEADER SLIDESHOW - REORDER FIRST 20 IMAGES TO END */
  const slideshowImages = [
    ...images.slice(20),   // all images from index 20 onward
    ...images.slice(0, 20) // first 20 images moved to the end
  ];

  useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slideshowImages.length);
    }, 4000);
    return () => clearInterval(slideInterval);
  }, []);

  /* FILTER MEDIA */
  const media =
    active === "All"
      ? [...videos, ...images]
      : active === "Videos"
      ? videos
      : images.filter((i) => i.category === active);

  /* SCROLL ANIMATION */
  useEffect(() => {
    const items = document.querySelectorAll(".gallery-item");
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("animate");
          }
        });
      },
      { threshold: 0.2 }
    );

    items.forEach((item) => observer.observe(item));
    return () => items.forEach((item) => observer.unobserve(item));
  }, [media]);

  return (
    <div className="gallery-page">
      {/* HEADER SLIDESHOW (ONLY IMAGES) */}
      <section className="header-slideshow">
        {slideshowImages.map((img, i) => (
          <div
            key={i}
            className={`slide ${i === currentSlide ? "active" : ""}`}
            style={{ backgroundImage: `url(${img.src})` }}
          />
        ))}
        <div className="slide-overlay">
          <div>
            <h1 style={{ color: "#1a4f8b" }}>RGM Family</h1>
            <p style={{ fontSize: "1.5rem", marginTop: "8px", color: "#ffffff" }}>
              Driven by Commitment • Powered by Precision • Delivering Trust Every Mile
            </p>
          </div>
        </div>
      </section>

      {/* FILTERS */}
      <div className="gallery-tabs">
        {categories.map((c) => (
          <button
            key={c}
            className={active === c ? "active" : ""}
            onClick={() => setActive(c)}
          >
            {c}
          </button>
        ))}
      </div>

      {/* MEDIA SECTION */}
      <div className="gallery-masonry">
        {media.map((item, i) => (
          <div className="gallery-item" key={i}>
            {item.type === "image" ? (
              <img
                src={item.src}
                loading="lazy"
                alt=""
                onClick={() => setLightbox(item.src)}
              />
            ) : (
              <video
                ref={i === currentVideo ? videoRef : null}
                src={item.src}
                muted
                playsInline
                autoPlay
                loop={false}
                onClick={() => setVideoModal(item.src)}
                onMouseEnter={(e) => e.target.play()}
                onMouseLeave={(e) => e.target.pause()}
              />
            )}
            <div className="gallery-overlay">
              <span>{item.category}</span>
            </div>
          </div>
        ))}
      </div>

      {/* IMAGE LIGHTBOX */}
      {lightbox && (
        <div className="lightbox" onClick={() => setLightbox(null)}>
          <img src={lightbox} alt="" />
        </div>
      )}

      {/* VIDEO MODAL */}
      {videoModal && (
        <div className="video-modal" onClick={() => setVideoModal(null)}>
          <video src={videoModal} controls autoPlay />
        </div>
      )}
    </div>
  );
};

export default Gallery;
